ChartScript.Script:
        function DrawChart(chart, data){


  var color = null;
  if(data.columns.c3.token != null)
  {
    var scaleFunc = scaleFor(data.columns.c3, data.rows.map(function(r){return r.c3;}), 0, 100, data.parameters["ColorScale"]);
   
    var colorQuantizer = d3.scale.quantize()
        .domain([0, 100])
        .range(colorbrewer[data.parameters["ColorSet"]][9]);
    
    color = function(v){return colorQuantizer(scaleFunc(v.c3)); }
  }
  else if(data.columns.c2.token != null) 
  {
    var categoryColor = d3.scale[data.parameters["ColorCategory"]]($.map(data.rows, function(v) { return v.c2; }));
    color = function(v) { return v.c2.color || categoryColor(v.c2); };
  }
  else
  {
    
    var categoryColor = d3.scale[data.parameters["ColorCategory"]]($.map(data.rows, function(v) { return v.c0; }));
    color = function(v) { return v.c0.color || categoryColor(v.c0); };
  }
  
  var format = d3.format(",d");
  
  var size = scaleFor(data.columns.c1, data.rows.map(function(r){return r.c1;}), 0, 1, data.parameters["Scale"]);
  
  var bubble = d3.layout.pack()
     .sort(null)
     .size([width, height])
     .padding(2)
     .value(function(r){return size(r.c1);});
  
  var tree = {
    children: data.rows
  }  
  
  var node = chart.selectAll("g.node")
      .data(bubble.nodes(tree).filter(function(d) { return !d.children; }))
    .enter().append("g")
      .attr("class", "node")
      .attr("transform", function(d) { return "translate(" + d.x + "," + d.y + ")"; });

  node.append("title")
      .text(function(d) { return d.c0.niceToString() + ": " + d.c1.niceToString(); });

  node.append("circle")
      .attr('shape-rendering', 'initial')
      .attr("r", function(d) { return d.r; })
      .style("fill", color)
      .style("fill-opacity", function(d) { return data.parameters["FillOpacity"]; })
      .style("stroke", function(d) { return data.parameters["StrokeColor"] || color(d); })
      .style("stroke-width", data.parameters["StrokeWidth"])
      .style("stroke-opacity", 1)
      .attr('data-click', function(p) { return getClickKeys(p, data.columns); })
      .append('svg:title')
  .text(function(d) { return d.c0.niceToString() + (data.columns.c2.token == null ? '' : (' (' + d.c2 + ')'))  + ': ' 
  + d.c1 + (data.columns.c3.token == null ? '' : (' (' + d.c3 + ')'));});

  var showNumber = data.parameters["NumberOpacity"] > 0;
  var numberSizeLimit = data.parameters["NumberSizeLimit"];
  
  
  node.append("text")
      .attr('dominant-baseline', 'central')
      .attr('text-anchor', 'middle')
  	  .attr("dy",function(d) { return  showNumber &&  d.r > numberSizeLimit ? "-0.5em" : null;})
      .attr('data-click', function(p) { return getClickKeys(p, data.columns); })
      .text(function(d) { return d.c0.niceToString(); })
      .each(function(d) { return ellipsis(this, d.r * 2, 1, ""); });
  
  if(showNumber)
  {
    node.filter(function(d) { return d.r > numberSizeLimit; })
    	.append("text")
        .attr('fill', "white")
        .attr('dominant-baseline', 'central')
        .attr('text-anchor', 'middle')
        .attr('font-weight', 'bold')
        .attr('opacity', data.parameters["NumberOpacity"])
        .attr("dy", ".5em")
        .attr('data-click', function(p) { return getClickKeys(p, data.columns); })
        .text(function(d) { return d.c1; })
        .each(function(d) { return ellipsis(this, d.r * 2, 1, ""); });
  }
  
}
