ChartScript.Script:
    function DrawChart(chart, data){
    
  function chooseSort(collection, value){
    if(value == "Ascending")
      return collection.orderBy(function(v){return v.key;});
    
    if(value == "Descending")
      return collection.orderByDescending(function(v){return v.key;});
    
    return collection;
  }
  
  var dim0 = data.rows
     .groupByArray(function(r){return r.c0; })
     .map(function(g){return g.elements[0].c0; });
     
  
 var dim1 = data.rows
     .groupByArray(function(r){return r.c1; })
     .map(function(g){return g.elements[0].c1; })
     .orderBy(function(v){return v.key;});
  
  dim0 = chooseSort(dim0, data.parameters["XSort"]);
  
  dim1 = chooseSort(dim1, data.parameters["YSort"]);

  var xRule = rule({
    _1 : 5,
    title : 15,
    _2 : 10, 
    labels : parseInt(data.parameters["XMargin"]),
    _3 : 5,
    ticks: 4,
    content: '*',
    _4: 10,
  }, width);
  //xRule.debugX(chart)
  
  var yRule = rule({
    _1 : 5,
    content: '*',
    ticks: 4,
    _2 : 5,
    labels0: 15,
    labels1: 15,
    _3 : 10,
    title: 15,
    _4 : 5,
  }, height);
  //yRule.debugY(chart);
  
  var x = d3.scale.ordinal()
      .domain(dim0)
      .rangeBands([0, xRule.size('content')]);
  
  var y = d3.scale.ordinal()
      .domain(dim1)
      .rangeBands([0, yRule.size('content')]);
  
 
  var color = null;
  if(data.columns.c3.token != null)
  {
    var scaleFunc = scaleFor(data.columns.c3, data.rows.map(function(r){return r.c3;}), 0, 100, data.parameters["ColorScale"]);
    
    var colorQuantizer = d3.scale.quantize()
        .domain([0, 100])
        .range(colorbrewer[data.parameters["ColorSet"]][9]);
    
    color = function(v){return colorQuantizer(scaleFunc(v)); }
  }
  
  chart.append('svg:g').attr('class', 'x-line').attr('transform',  translate(xRule.start('content') + x.rangeBand() / 2, yRule.start('content')))
    .enterData(dim0, 'line', 'y-line')
     .attr('y2',  yRule.size('content'))
      .attr('x1', function (d) { return x(d); })
      .attr('x2', function (d) { return x(d); })
    .style('stroke', 'LightGray');
  
  chart.append('svg:g').attr('class', 'x-tick').attr('transform', translate(xRule.start('content') + x.rangeBand() / 2, yRule.start('ticks')))
    .enterData(dim0, 'line', 'x-tick')
      .attr('y2',  yRule.size('ticks'))
      .attr('x1', function (d) { return x(d); })
      .attr('x2', function (d) { return x(d); })
      .style('stroke', 'Black');
  
  if ((x.rangeBand() * 2) > 60)     
  {
    chart.append('svg:g').attr('class', 'x-label').attr('transform', translate(xRule.start('content')+ (x.rangeBand() / 2), yRule.middle('labels0')))
      .enterData(dim0, 'text', 'x-label')
        .attr('x', function (d) { return x(d); })
        .attr('y', function (d, i) { return yRule.middle('labels' + (i % 2)) - yRule.middle('labels0'); })
        .attr('dominant-baseline', 'middle')
        .attr('text-anchor', 'middle')
        .text(function (d) { return d.niceToString(); })
        .each(function (v) { ellipsis(this, x.rangeBand() * 1.7); });
  }
  
  chart.append('svg:g').attr('class', 'x-title').attr('transform', translate(xRule.middle('content'), yRule.middle('title')))
    .append('svg:text').attr('class', 'x-title')
    .attr('text-anchor', 'middle')
    .attr('dominant-baseline', 'middle')
    .text(data.columns.c0.title);
  

  
  chart.append('svg:g').attr('class', 'y-line').attr('transform', translate(xRule.start('content'), yRule.end('content') - y.rangeBand() / 2))
    .enterData(dim1, 'line', 'y-line')
    .attr('x2', xRule.size('content'))
    .attr('y1', function (t) { return -y(t); })
    .attr('y2', function (t) { return -y(t); })
    .style('stroke', 'LightGray');
  
  chart.append('svg:g').attr('class', 'y-tick').attr('transform', translate(xRule.start('ticks'), yRule.end('content') - y.rangeBand() / 2))
    .enterData(dim1, 'line', 'y-tick')
    .attr('x2', xRule.size('ticks'))
    .attr('y1', function (t) { return -y(t); })
    .attr('y2', function (t) { return -y(t); })
    .style('stroke', 'Black');
  
   if (y.rangeBand() > 16)     
   {
      chart.append('svg:g').attr('class', 'y-label').attr('transform',  translate(xRule.end('labels'), yRule.end('content') - y.rangeBand() / 2))
        .enterData(dim1, 'text', 'y-label')
        .attr('y', function (t) { return -y(t); })
        .attr('dominant-baseline', 'middle')
        .attr('text-anchor', 'end')
        .text(function (d) { return d.niceToString(); })
        .each(function (v) { ellipsis(this, xRule.size('labels')); });  
	}
 
  chart.append('svg:g').attr('class', 'y-label').attr('transform', translate(xRule.middle('title'), yRule.middle('content')) + rotate(270))
    .append('svg:text').attr('class', 'y-label')
    .attr('text-anchor', 'middle')
    .attr('dominant-baseline', 'middle')
    .text(data.columns.c1.title);
  
   var isCircle =  data.parameters["Shape"] == "Circle";
  
  var shapes = chart.enterData(data.rows, isCircle ? 'circle': 'rect', 'punch').attr('transform', translate(xRule.start('content') + x.rangeBand() / 2, yRule.end('content') - y.rangeBand() / 2))
      .filter(function(r) {return r != undefined;})
      .attr('fill', function(r) { return color == null? (data.parameters["FillColor"] || 'black'):  color(r.c3); })
      .attr('fill-opacity', data.parameters["ColorOpacity"])
      .attr('stroke', function(r) { return data.parameters["StrokeColor"] || (color == null? 'black': color(r.c3)); })
      .attr('stroke-width',  data.parameters["StrokeWidth"])
      .attr("shape-rendering","initial")
      .attr('data-click', function(r) { return getClickKeys(r, data.columns); });
  
  if(isCircle)
  {
     var circleSize = Math.min(x.rangeBand(), y.rangeBand()) * 0.45;
     var area = data.columns.c2.token == null? null : scaleFor(data.columns.c2, data.rows.map(function(r){return r.c2;}), 0, circleSize * circleSize, data.parameters["SizeScale"]);
    
     shapes.attr('cx', function(r, i) { return x(r.c0); })
      .attr('cy', function(r) { return -y(r.c1); })
      .attr('r', function(r) { return area == null? circleSize: Math.sqrt(area(r.c2)); });
    
  }else{
    
     var area = data.columns.c2.token == null? null : scaleFor(data.columns.c2, data.rows.map(function(r){return r.c2;}), 0, x.rangeBand() * y.rangeBand(), data.parameters["SizeScale"]); 
     var ratio = x.rangeBand() / y.rangeBand();
     var recWidth = function(r) { return area == null? x.rangeBand(): Math.sqrt(area(r.c2) * ratio); };
     var recHeight = function(r) { return area == null? y.rangeBand(): Math.sqrt(area(r.c2) / ratio); };
     
    shapes.attr('x', function(r, i) { return x(r.c0) - recWidth(r) / 2; })
      .attr('y', function(r) { return -y(r.c1) - recHeight(r) / 2; })
      .attr('width', recWidth)
      .attr('height', recHeight);
    
  }
  
  if(data.parameters["NumberOpacity"] > 0)
  {
      var two = data.columns.c2.token != null && data.columns.c3.token != null;
    
    if(data.columns.c2.token != null)
    {
      chart.enterData(data.rows, 'text', 'punch').attr('transform', translate(xRule.start('content') + x.rangeBand() / 2, yRule.end('content') - y.rangeBand() / 2))
      .attr('x', function(r, i) { return x(r.c0); })
      .attr('y', function(r) { return -y(r.c1) + (two? -10: 0); })
      .attr('fill', 'white')
      .attr('dominant-baseline', 'central')
      .attr('opacity', data.parameters["NumberOpacity"])
      .attr('text-anchor', 'middle')
      .attr('font-weight', 'bold')
      .text(function (r) { return r.c2; });
    }
    
     if(data.columns.c3.token != null)
     {
       chart.enterData(data.rows, 'text', 'punch2').attr('transform', translate(xRule.start('content') + x.rangeBand() / 2, yRule.end('content') - y.rangeBand() / 2))
        .attr('x', function(r, i) { return x(r.c0); })
        .attr('y', function(r) { return -y(r.c1) + (two? 10: 0); })
        .attr('fill', 'white')
        .attr('dominant-baseline', 'central')
        .attr('opacity', data.parameters["NumberOpacity"])
        .attr('text-anchor', 'middle')
        .attr('font-weight', 'bold')
        .text(function (r) { return  r.c3; });
     }
  }
  
  shapes.append('svg:title')
      .text(function(r) { return r.c0.niceToString() + ', ' + r.c1.niceToString() + 
        (data.columns.c2.token == null? "" : ("\n" + data.columns.c2.title +": " +r.c2.niceToString()) ) +
        (data.columns.c3.token == null? "" : ("\n" +  data.columns.c3.title +": " +r.c3.niceToString()) ); });
  
  chart.append('svg:g').attr('class', 'x-axis').attr('transform', translate(xRule.start('content'), yRule.end('content')))
    .append('svg:line')
    .attr('class', 'x-axis')
    .attr('x2', xRule.size('content'))
    .style('stroke', 'Black');
  
  chart.append('svg:g').attr('class', 'y-axis').attr('transform', translate(xRule.start('content'), yRule.start('content')))
    .append('svg:line')
    .attr('class', 'y-axis')
    .attr('y2', yRule.size('content'))
    .style('stroke', 'Black'); 
  
}
